mapper.select:
select username, userid, email
from `member`
where userid = #{userId} and userpwd = #{userPwd}

select userid, userpwd, username, email, date_format(joindate, '%y.%m.%d') joindate
from `member`
order by joindate desc;

select userid, userpwd, username, email, date_format(joindate, '%y.%m.%d') joindate
from `member`
where userid = #{userid}
